$txt[$i++] = "\\(\\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right) \\times [i:n,j]\\left(\\left(-1\\right)^{i + j} {\\binom{j + 1}{i + 1}}\\right) = \\left(\\begin{array}{c}{[j]\\sum\\limits_{k=0}^{n - 1} \\left(-1\\right)^{j + k} \\left(1 - r^{k + 1}\\right) {\\binom{j + 1}{k + 1}}}\\\\{[i:n - 1,j]\\sum\\limits_{k=0}^{n - 1} \\left(-1\\right)^{j + k} \\left(k + 1\\right)^{i + 1} {\\binom{j + 1}{k + 1}}}\\end{array}\\right)\\tag*{Eq[1]}\\)";
$txt[$i++] = "\\(\\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right) \\times [i:n,j]\\left(\\left(-1\\right)^{i + j} {\\binom{j + 1}{i + 1}}\\right) = \\left(\\begin{array}{c}{[j]\\sum\\limits_{k=0}^{n - 1} \\left(-1\\right)^{j + k} \\left(1 - r^{k + 1}\\right) {\\binom{j + 1}{k + 1}}}\\\\{[i:n - 1,j]\\sum\\limits_{k=0}^{j} \\left(-1\\right)^{j + k} \\left(k + 1\\right)^{i + 1} {\\binom{j + 1}{k + 1}}}\\end{array}\\right)\\tag*{Eq[2]}\\)";
