$txt[$i++] = "\\(0 = \\left(-1\\right)^{j} \\sum\\limits_{k=0}^{j + 1} \\left(-1\\right)^{k} {\\binom{j + 1}{k}}\\tag*{Eq[20]}\\)";
$txt[$i++] = "\\(\\sum\\limits_{k=0}^{j} \\left(-1\\right)^{j + k} \\left(1 - r^{k + 1}\\right) {\\binom{j + 1}{k + 1}} = \\left(-1\\right)^{j} \\sum\\limits_{k=0}^{j + 1} \\left(-1\\right)^{k} {\\binom{j + 1}{k}} - \\left(-1\\right)^{j} \\sum\\limits_{k=1}^{j + 1} \\left(-1\\right)^{k} {\\binom{j + 1}{k}} - \\left(-1\\right)^{j} - \\left(r - 1\\right)^{j + 1}\\tag*{Eq[21]}\\)";
$txt[$i++] = "\\(\\sum\\limits_{k=0}^{j} \\left(-1\\right)^{j + k} \\left(1 - r^{k + 1}\\right) {\\binom{j + 1}{k + 1}} = - \\left(r - 1\\right)^{j + 1}\\tag*{Eq[22]}\\)";
$txt[$i++] = "\\(\\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right) \\times [i:n,j]\\left(\\left(-1\\right)^{i + j} {\\binom{j + 1}{i + 1}}\\right) = \\left(\\begin{array}{c}{[j]\\left(- \\left(r - 1\\right)^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(\\left\\{\\begin{matrix}{i + 1}\\\\{j + 1}\\end{matrix}\\right\\} \\left(j + 1\\right)!\\right)}\\end{array}\\right)\\tag*{Eq[23]}\\)";
$txt[$i++] = "\\(Shift\\left(n, 0, n - 1\\right) \\times \\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right) \\times [i:n,j]\\left(\\left(-1\\right)^{i + j} {\\binom{j + 1}{i + 1}}\\right) = \\left(\\begin{array}{c}{[i:n - 1,j]\\left(\\left\\{\\begin{matrix}{i + 1}\\\\{j + 1}\\end{matrix}\\right\\} \\left(j + 1\\right)!\\right)}\\\\{[j]\\left(- \\left(r - 1\\right)^{j + 1}\\right)}\\end{array}\\right)\\tag*{Eq[24]}\\)";
$txt[$i++] = "\\(\\left(-1\\right)^{n + 1} \\left|{\\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right)}\\right| = \\prod\\limits_{k=0}^{n - 1} \\begin{cases} \\left(k + 1\\right)! & \\text{if}\\: k < n - 1 \\\\- \\left(r - 1\\right)^{k + 1} & \\text{if}\\: k < n \\end{cases}\\tag*{Eq[25]}\\)";
$txt[$i++] = "\\(\\left(-1\\right)^{n + 1} \\left|{\\left(\\begin{array}{c}{[j]\\left(1 - r^{j + 1}\\right)}\\\\{[i:n - 1,j]\\left(j + 1\\right)^{i + 1}}\\end{array}\\right)}\\right| = - \\left(r - 1\\right)^{n} \\prod\\limits_{k=0}^{n - 2} \\left(k + 1\\right)!\\tag*{Eq[26]}\\)";
